CmdInfo GetCmdInfo(Cmd command)
{
    for (size_t i = 0; i < CmdInfoArrSize; i++)
    {
        CmdInfo cmd_info = CmdInfoArr[i];
    
        if (command == cmd_info.cmd)
            return cmd_info;
    }

    CmdInfo undef_command = {.cmd = Cmd::undef_cmd};

    return undef_command;
}

