#include <string.h>
#include <assert.h>
#include "global/global_include.hpp"

//--------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------//--------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------//--------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------

const char* GetFileExtension(const char* file_name)
{
    assert(file_name);

    const char* dot = strrchr(file_name, '.');

    if ((!dot) || (dot == file_name))
        return nullptr;

    return dot + 1;
}

